    def on_combobox_changed(self, value):
        print("combobox changed", value)
        conexion = sqlite3.connect('movies.db')
        if conexion == None:
            print("Hola")
        else:
            print('Funciona')
        cur = conexion.cursor()
        cur.execute('SELECT movieId FROM ratings WHERE userId != ?', value)
        resultado = cur.fetchall()
        PelisNoVistas = []
        for [x] in resultado:
            var = str(x)
            PelisNoVistas.append(var)
        self.comboBox_Usuario2.addItem(PelisNoVistas)
        conexion.close()
        cur.close()


